import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { gameById } from "../../Redux/Actions";

function Detail() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const details = useSelector((state) => state.videogameDetail);
  const genres = details.genres.map((genre, i) => (<div key={genre}><span>{genre}</span>{i < details.genres.length - 1 && ', '}</div>))
  useEffect(() => {
    dispatch(gameById(id));
  }, []);
  console.log(details);
  return (
    <div>
      <h1>{details.name}</h1>
      <img src={details.image} alt={details.name} />
      <h3>Genres: {genres}</h3>
      <h3>Rating: {details.rating}</h3>
      <h3>Released: {details.released}</h3>
      <h3>Description</h3>
      <div dangerouslySetInnerHTML={{ __html: details.description }} />
    </div>
  );
}

export default Detail;
