import { ALL_GAMES, CRETATE_GAME, GAME_BY_ID, UPDATE_GAME } from "./Actions";

const initState = {
  videogames: [],
  videogamesBackUp: [],
  videogameDetail: {}
};

const reducer = (state = initState, { type, payload }) => {
  switch (type) {
    case ALL_GAMES:
      return {
        ...state,
        videogames: [...payload],
        videogamesBackUp: [...payload],
      };
    case GAME_BY_ID:
      if (videogameDetail.id)
      return {
        ...state,
        videogameDetail: payload
      };
    case CRETATE_GAME:
      return {
        ...state,
        videogames: [...state.videogames, payload],
        videogamesBackUp: [...state.videogamesBackUp, payload]
      };
    case UPDATE_GAME:
      return {
        ...state,
        videogameDetail: Object.assign(state.videogameDetail, payload)
      }
    default:
      return state;

  }
};

export default reducer;
