import {
  ALL_GAMES,
  CRETATE_GAME,
  DELETE_GAME,
  FILTER,
  GAME_BY_ID,
  RESTART,
  RESTORE,
  UPDATE_GAME,
} from "./Actions";

const initState = {
  videogames: [],
  videogamesBackUp: [],
  videogameDetail: {},
  message: "",
};

const reducer = (state = initState, { type, payload }) => {
  switch (type) {
    case ALL_GAMES:
      return {
        ...state,
        videogames: [...payload],
        videogamesBackUp: [...payload],
      };
    case GAME_BY_ID:
      return {
        ...state,
        videogameDetail: payload,
      };
    case CRETATE_GAME:
      return {
        ...state,
        message: payload,
      };
    case UPDATE_GAME:
      return {
        ...state,
        message: payload,
      };
    case DELETE_GAME:
      return {
        ...state,
        message: payload,
      };
    case RESTART:
      return {
        ...state,
        videogameDetail: {},
      };
    case RESTORE:
      return {
        ...state,
        message: "",
      };
      case FILTER:    
      if ( payload.value === 'All') {
        return {
          ...state,
          videogames: state.videogamesBackUp
        }
      } else {
        
      }    
          
    default:
      return state;
  }
};

export default reducer;
