import { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { gameById, restart } from "../../Redux/Actions";

function Detail() {
  const boolRef = useRef(true)
  const navigate = useNavigate();
  const { id } = useParams();
  const uuidRegex =
    /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
  const isUuid = uuidRegex.test(id);
  const dispatch = useDispatch();
  const details = useSelector((state) => state.videogameDetail);

  useEffect(() => {
    dispatch(gameById(id));

    return () => {
      if (boolRef.current) {
        dispatch(restart());
      }
    };
  }, [bool]);

  const handleClick = () => {
    boolRef.current = false;
    navigate(`/update/${id}`);
  };

  return (
    <div>
      <h1>{details.name}</h1>
      <img src={details.image} alt={details.name} />
      {details.genres && (
        <h4>
          {details.genres.map((plat, i) => (
            <div key={i}>
              <span>{plat}</span> {i < details.genres.length - 1 && ", "}
            </div>
          ))}
        </h4>
      )}
      {details.platforms && (
        <h4>
          {details.platforms.map((plat, i) => (
            <div key={i}>
              <span>{plat}</span> {i < details.platforms.length - 1 && ", "}
            </div>
          ))}
        </h4>
      )}
      <h3>Rating: {details.rating}</h3>
      <h3>Released: {details.released}</h3>
      <h3>Description</h3>
      <div dangerouslySetInnerHTML={{ __html: details.description }} />
      {isUuid && (
        <div>
          <button onClick={handleClick}>Update Game</button>
          <button>Delete Game</button>
        </div>
      )}
    </div>
  );
}

export default Detail;
