import {
  ALL_GAMES,
  CRETATE_GAME,
  GAME_BY_ID,
  RESTART,
  UPDATE_GAME,
} from "./Actions";

const initState = {
  videogames: [],
  videogamesBackUp: [],
  videogameDetail: {},
  message: ''
};

const reducer = (state = initState, { type, payload }) => {
  switch (type) {
    case ALL_GAMES:
      return {
        ...state,
        videogames: [...payload],
        videogamesBackUp: [...payload],
      };
    case GAME_BY_ID:
      return {
        ...state,
        videogameDetail: payload,
      };
    case CRETATE_GAME:
      return {
        ...state,
        message: payload
      };
    case UPDATE_GAME:
      return {
        ...state,
        message: payload
      };
    case DELETE_GAME:
      return {
        ...state,
      }  
    case RESTART:
      return {
        ...state,
        videogameDetail: payload,
      };
    default:
      return state;
  }
};

export default reducer;
