import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { createGame } from "../../Redux/Actions";
import Genres from "../Genres/genres";
import Platforms from "../Platforms/platforms";

function Form() {
  const dispatch = useDispatch()
  const [form, setForm] = useState({
    name: "",
    image: "",
    genres: [],
    platforms: [],
    rating: '',
    released: "",
    description: ''
  });

  useEffect(() => {
    return () => URL.revokeObjectURL(imageUrl)
  }, [])

  const handleForm = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    if (name === "rating" && value > 0 && value <= 5) {
      setForm({
        ...form,
        [name]: value,
      });
    } else if (name === 'image') {
      const file = event.target.files[0];
      console.log(file);
      const url = URL.createObjectURL(file)
      setForm({
        ...form,
        [name]: url
      })
    } else if (name !== "rating") {
      setForm({
        ...form,
        [name]: value,
      });
    }
  };

  const handleOptions = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    if (form[name].includes(value)) return;
    setForm({
      ...form,
      [name]: [...form[name], value],
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(createGame(form))
    window.alert('Your videogame was saved successfuly')
  }

  
  // console.log(URL.createObjectURL(form.image));
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          onChange={handleForm}
          type="text"
          placeholder="Title"
          name="name"
          value={form.name}
          autoComplete="off"
        />
        <input
          onChange={handleForm}
          type="file"
          placeholder="Upload image"
          name="image"
          autoComplete="off"
        />
        <img src={form.image}/>
        <Genres handleChange={handleOptions} />
        <div>{form.genres.join(", ")}</div>
        <Platforms handleChange={handleOptions} />
        <div>{form.platforms.join(", ")}</div>
        <input
          onChange={handleForm}
          type="number"
          placeholder="Rating"
          name="rating"
          value={form.rating}
          autoComplete="off"
        />
        <input
          onChange={handleForm}
          type="date"
          placeholder="Released Date"
          name="released"
          value={form.released}
          autoComplete="off"
        />
        <textarea
          onChange={handleForm}
          placeholder="Description..."
          name="description"
          value={form.description}
          autoComplete="off"
        />
        <button>SUBMIT</button>
      </form>
    </div>
  );
}

export default Form;
