import {
  ALL_GAMES,
  CRETATE_GAME,
  DELETE_GAME,
  FILTER,
  GAME_BY_ID,
  RESTART,
  RESTORE,
  UPDATE_GAME,
} from "./Actions";

const initState = {
  videogames: [],
  videogamesAux: [],
  videogamesBackUp: [],
  videogameDetail: {},
  message: "",
};

const reducer = (state = initState, { type, payload }) => {
  switch (type) {
    case ALL_GAMES:
      return {
        ...state,
        videogames: [...payload],
        videogamesAux: [...payload],
        videogamesBackUp: [...payload],
      };
    case GAME_BY_ID:
      return {
