import Genres from "../Genres/genres";
import Platforms from "../Platforms/platforms";

function Form({ handleEdit, handleSubmit, handleOptions, handleForm, handleImg, name, image, genres, platforms, rating, released, description}) {
  
  const img = (img) => {
    if (img && img.name.includes('http')) {
      const url = URL.createObjectURL(img)
      return url;
    } return img
  }; 
  
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          onChange={handleForm}
          type="text"
          placeholder="Title"
          name="name"
          value={name}
          autoComplete="off"
        />
        <input
          onChange={handleImg}
          type="file"
          placeholder="Upload image"
          name="image"
        />
        <img src={img(image)} alt={name}/>
        <Genres handleChange={handleOptions} />
        {genres.map((genre, i) => <button type='button' key={i} name='genres' value={genre} onClick={handleEdit}>{genre+ ' x'}</button>)}
        <Platforms handleChange={handleOptions} />
        {platforms.map((plat, i) => <button type='button' key={i} name='platforms' value={plat}onClick={handleEdit}>{plat+ ' x'}</button>)}
        <input
          onChange={handleForm}
          type="number"
          placeholder="Rating"
          name="rating"
          value={rating}
          autoComplete="off"
        />
        <input
          onChange={handleForm}
          type="date"
          placeholder="Released Date"
          name="released"
          value={released}
          autoComplete="off"
        />
        <textarea
          onChange={handleForm}
          placeholder="Description..."
          name="description"
          value={description}
          autoComplete="off"
        />
        <button type='submit'>SUBMIT</button>
      </form>
    </div>
  );
}

export default Form;
