import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { gameById } from "../../Redux/Actions";

function Home() {
  const videogames = useSelector((state) => state.videogames);
  const details = use
  const dispatch = useDispatch();

  const handleClick = (id) => {
    dispatch(gameById(id));
  };

  const renderGames = videogames.map((games) => {
    return (
      <div key={games.id}>
        <img src={games.image} alt={games.name} />
        <Link to={`/detail/${games.id}`}>
          <h3 onClick={() => handleClick(games.id)}>{games.name}</h3>
        </Link>

        <h4>
          {games.genres.map((genre, i) => (
            <div key={i}>
              <span>{genre}</span> {i < games.genres.length - 1 && ", "}
            </div>
          ))}
        </h4>
      </div>
    );
  });

  return <div>{renderGames}</div>;
}

export default Home;
