import Genres from "../Genres/genres";
import Platforms from "../Platforms/platforms";

function Form({handleSubmit, handleOptions, handleForm, handleImg, name, image, genres, platforms, rating, released, description}) {
  const handleFilter = () => {

  }
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          onChange={handleForm}
          type="text"
          placeholder="Title"
          name="name"
          value={name}
          autoComplete="off"
        />
        <input
          onChange={handleImg}
          type="file"
          placeholder="Upload image"
          name="image"
        />
        <img src={image} />
        <Genres handleChange={handleOptions} />
        {genres.map((genr, i) => <button onClick={handleFilter}>{genre}</button>)}
        <Platforms handleChange={handleOptions} />
        <div>{platforms.join(", ")}</div>
        <input
          onChange={handleForm}
          type="number"
          placeholder="Rating"
          name="rating"
          value={rating}
          autoComplete="off"
        />
        <input
          onChange={handleForm}
          type="date"
          placeholder="Released Date"
          name="released"
          value={released}
          autoComplete="off"
        />
        <textarea
          onChange={handleForm}
          placeholder="Description..."
          name="description"
          value={description}
          autoComplete="off"
        />
        <button>SUBMIT</button>
      </form>
    </div>
  );
}

export default Form;
