import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { gameById, restart } from "../../Redux/Actions";

function Detail() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const details = useSelector((state) => state.videogameDetail);

  useEffect(() => {
    dispatch(gameById(id));
    return () => {
      dispatch(restart());
    };
  }, [de]);

  console.log(details);

  return (
    <div>
      <h1>{details.name}</h1>
      <img src={details.image} alt={details.name} />

      <h4>
        {details.genres.map((plat, i) => (
          <div key={i}>
            <span>{plat}</span> {i < details.genres.length - 1 && ", "}
          </div>
        ))}
      </h4>

      <h4>
        {details.platforms.map((plat, i) => (
          <div key={i}>
            <span>{plat}</span> {i < details.platforms.length - 1 && ", "}
          </div>
        ))}
      </h4>

      <h3>Rating: {details.rating}</h3>
      <h3>Released: {details.released}</h3>
      <h3>Description</h3>
      <div dangerouslySetInnerHTML={{ __html: details.description }} />
    </div>
  );
}

export default Detail;
