import { useRef, useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import style from "./home.module.css";

function Home() {
  const videogames = useSelector((state) => state.videogames);
  const quantityPage = useRef(4);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [lastPosition, setLastPosition] = useState(15);
  const renderGames = videogames
    .slice(currentIndex, lastPosition)
    .map((games) => {
      return (
        <div className={style.game} key={games.id}>
          <img className={style.image} src={games.image} alt={games.name} />
          <div className={style.text}>
            <Link to={`/detail/${games.id}`}>
              <h3>{games.name}</h3>
            </Link>
            <div>
              {games.genres.map((genre, i) => (
                <>
                  <span>{genre}</span> {i < games.genres.length - 1 && "   "}
                </>
              ))}
            </div>
          </div>
        </div>
      );
    });

  return (
    <div>
      <div className={style.home}>{renderGames}</div>
      <div>{Array(Math.ceil(videogames.length / quantityPage.current)).map((_, i) => <button>{i + 1}</button>)}</div>
    </div>
  );
}

export default Home;
