import { useState } from "react";
import Genres from "../Genres/genres";
import Platforms from "../Platforms/platforms";

function Form() {
  const [form, setForm] = useState({
    title: "",
    img: "",
    genres: [],
    platforms: [],
    rating: '',
    released: "",
    description: ''
  });

  const handleForm = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    if (name === "rating" && value > 0 && value <= 5) {
      setForm({
        ...form,
        [name]: value,
      });
    } else if (name !== "rating") {
      setForm({
        ...form,
        [name]: value,
      });
    }
  };

  const handleOptions = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    if (form[name].includes(value)) return;
    setForm({
      ...form,
      [name]: [...form[name], value],
    });
  };

  console.log(form);
  return (
    <div>
      <form >
        <input
          onChange={handleForm}
          type="text"
          placeholder="Title"
          name="title"
          value={form.title}
          autoComplete="off"
        />
        <input
          onChange={handleForm}
          type="file"
          placeholder="Upload image"
          name="img"
          value={form.img}
          autoComplete="off"
        />
        <Genres handleChange={handleOptions} />
        <div>{form.genres.join(", ")}</div>
        <Platforms handleChange={handleOptions} />
        <div>{form.platforms.join(", ")}</div>
        <input
          onChange={handleForm}
          type="number"
          placeholder="Rating"
          name="rating"
          value={form.rating}
          autoComplete="off"
        />
        <input
          onChange={handleForm}
          type="date"
          placeholder="Released Date"
          name="released"
          value={form.released}
          autoComplete="off"
        />
        <textarea
          onChange={handleForm}
          placeholder="Description..."
          name="description"
          value={form.description}
          autoComplete="off"
        />
        <button>SUBMIT</button>
      </form>
    </div>
  );
}

export default Form;
