import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import ReactPaginate from "react-paginate";
import { filter, order } from "../../Redux/Actions";
import Genres from "../Genres/genres";
import Platforms from "../Platforms/platforms";
import style from "./home.module.css";

function Home() {
  const [_renderOrder, setRenderOrder] = useState('')
  const dispatch = useDispatch();
  const videogames = useSelector((state) => state.videogames);
  const render = true;
  
  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    if (value) {
      const options = { name, value };
      dispatch(filter(options));
    }
    
  };

  const handleOrder = (event) => {
    const value = event.target.value;
    value && dispatch(order(value))
    setRenderOrder(value)
  }

  console.log(videogames);

  const gamesPerPage = 15;
  const [gameOffset, setGameOffset] = useState(0);
  const endOffset = gameOffset + gamesPerPage;
  const currentGames = videogames.slice(gameOffset, endOffset);
  const pageCount = <Mathceil></Mathceil>

  const renderGames = videogames.map((games) => {
    return (
      <div className={style.game} key={games.id}>
        <img className={style.image} src={games.image} alt={games.name} />
        <div className={style.text}>
          <Link to={`/detail/${games.id}`}>
            <h3>{games.name}</h3>
          </Link>
          <div>
            {games.genres.map((genre, i) => (
              <>
                <span key={i}>{genre}</span>{" "}
                {i < games.genres.length - 1 && "   "}
              </>
            ))}
          </div>
        </div>
      </div>
    );
  });

  

  return (
    <div className={style.bigContainer}>
      <div className={style.filters}>
        <Genres className={style.buttons} handleChange={handleChange} render={render} />
        <Platforms className={style.buttons} handleChange={handleChange} render={render} />
        <select className={style.buttons} name='id' onChange={handleChange}>
          <option value=''>Filter by storage</option>
          <option value='database'>Database</option>
          <option value='api'>API</option>
          <option value='All'>All</option>
        </select>
        <select className={style.buttons} onChange={handleOrder}>
          <option value=''>Order alphabetically</option>
          <option value='a-z'>A -{'>'} Z</option>
          <option value='z-a'>Z -{'>'} A</option>
        </select>
        <select className={style.buttons} onChange={handleOrder}>
          <option value=''>Order by rating</option>
          <option value='ascending'>Ascending</option>
          <option value='descending'>Descending</option>
        </select>
      </div>
      <div className={style.cards}>{renderGames}</div>
     
    </div>
  );
}

export default Home;
