import { useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { allGames, deleteGame, gameById, restart } from "../../Redux/Actions";
import style from "./detail.module.css";

function Detail() {
  const boolRef = useRef(true);
  const navigate = useNavigate();
  const message = useSelector((state) => state.message);
  const { id } = useParams();
  const uuidRegex =
    /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
  const isUuid = uuidRegex.test(id);
  const dispatch = useDispatch();
  const details = useSelector((state) => state.videogameDetail);

  console.log(details);

  useEffect(() => {
    dispatch(gameById(id));

    return () => {
      if (boolRef.current) {
        dispatch(restart());
      }
    };
  }, []);

  const handleUpdate = () => {
    boolRef.current = false;
    navigate(`/update/${id}`);
  };

  const handleDelete = () => {
    dispatch(deleteGame(id));
    dispatch(allGames());
  };

  return (
    <div className={style.container}>
      {message ? (
        <span>{message}</span>
      ) : (
        <>
          <h1>{details.name}</h1>
          <img src={details.image} alt={details.name} />
          {details.genres && (
            <>
              <span>
                Genres:
                {details.genres.map((genre, i) => (
                  <>
                    {" " + genre}{i < details.genres.length - 1 && ","}
                  </>
                ))}
              </span>
            </>
          )}
          {details.platforms && (
            <>
              <span>Platforms:

              </span>
              
            </>
          )}
          <h3>Rating: {details.rating}</h3>
          <h3>Released: {details.released}</h3>
          <h3>Description:</h3>
          <div dangerouslySetInnerHTML={{ __html: details.description }} />
          {isUuid && (
            <div>
              <button onClick={handleUpdate}>Update Game</button>
              <button onClick={handleDelete}>Delete Game</button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default Detail;
