import { useSelector } from "react-redux";

function Detail() {
  const { name, image, platforms, released, rating, genres, description } =
    useSelector((state) => state.videogameDetail);

  return (
    <div>
      <h1>{name}</h1>
      <img src={image} alt={name} />
      <div>
        {platforms.map((plat, i) => {
          <div key={i}>
            <span>{plat}</span>
            {i < platforms.length - 1 && ", "}
          </div>;
        })}
      </div>
      <h3>{released}</h3>
      <h3>{rating}</h3>
      <h3>{
        genres.map((genre, i) => {
          <div key={i}><span>{genre}</span> {i < genres.length - 1} </div>
        })
        }</h3>
    </div>
  );
}

export default Detail;
