import Genres from "../Genres/genres";
import Platforms from "../Platforms/platforms";
import style from "./form.module.css";

function Form({
  handleEdit,
  handleSubmit,
  handleOptions,
  handleForm,
  handleImg,
  name,
  image,
  genres,
  platforms,
  rating,
  released,
  description,
}) {
  const img = (img) => {
    if (img) {
      if (img.name) return URL.createObjectURL(img);
      return img;
    }
    return img;
  };

  return (
    <div>
      <form className={style.form} onSubmit={handleSubmit}>
        <div>
          <label>Title</label>
          <input
            onChange={handleForm}
            type="text"
            placeholder="Title"
            name="name"
            value={name}
            autoComplete="off"
          />
        </div>
        <div>
          <label>Image</label>
          <input
            onChange={handleImg}
            type="file"
            placeholder="Upload image"
            name="image"
          />
          <img src={img(image)} alt={name} />
        </div>
        <div></div>

        <label>Genres</label>
        <Genres handleChange={handleOptions} />
        {genres.map((genre, i) => (
          <button
            type="button"
            key={i}
            name="genres"
            value={genre}
            onClick={handleEdit}
          >
            {genre + " x"}
          </button>
        ))}
        <label>Platforms</label>
        <Platforms handleChange={handleOptions} />
        {platforms.map((plat, i) => (
          <button
            type="button"
            key={i}
            name="platforms"
            value={plat}
            onClick={handleEdit}
          >
            {plat + " x"}
          </button>
        ))}
        <label>Rating</label>
        <input
          onChange={handleForm}
          type="number"
          placeholder="Rating"
          name="rating"
          value={rating}
          autoComplete="off"
        />
        <label>Released</label>
        <input
          onChange={handleForm}
          type="date"
          placeholder="Released Date"
          name="released"
          value={released}
          autoComplete="off"
        />
        <label>Description</label>
        <textarea
          onChange={handleForm}
          placeholder="Description..."
          name="description"
          value={description}
          autoComplete="off"
        />
        <button type="submit">SUBMIT</button>
      </form>
    </div>
  );
}

export default Form;
