import { useState } from "react";
import { useDispatch } from "react-redux";
import { createGame } from "../../Redux/Actions";
import Genres from "../Genres/genres";
import Platforms from "../Platforms/platforms";

function Form() {
  
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          onChange={handleForm}
          type="text"
          placeholder="Title"
          name="name"
          value={form.name}
          autoComplete="off"
        />
        <input
          onChange={handleForm}
          type="file"
          placeholder="Upload image"
          name="image"
          autoComplete="off"
        />
        <Genres handleChange={handleOptions} />
        <div>{form.genres.join(", ")}</div>
        <Platforms handleChange={handleOptions} />
        <div>{form.platforms.join(", ")}</div>
        <input
          onChange={handleForm}
          type="number"
          placeholder="Rating"
          name="rating"
          value={form.rating}
          autoComplete="off"
        />
        <input
          onChange={handleForm}
          type="date"
          placeholder="Released Date"
          name="released"
          value={form.released}
          autoComplete="off"
        />
        <textarea
          onChange={handleForm}
          placeholder="Description..."
          name="description"
          value={form.description}
          autoComplete="off"
        />
        <button>SUBMIT</button>
      </form>
    </div>
  );
}

export default Form;
