import { useState } from "react";
import { useDispatch } from "react-redux";
import { filter, order } from "../../Redux/Actions";
import Genres from "../Genres/genres";
import Platforms from "../Platforms/platforms";
import Videogames from "../Videogames/videogames";
import style from "./home.module.css";

function Home() {
  const [_renderOrder, setRenderOrder] = useState("");
  const [gameOffset, setGameOffset] = useState(0);
  const dispatch = useDispatch()
  const render = true;
 
  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    if (value) {
      const options = { name, value };
      dispatch(filter(options));
    }
    setGameOffset(0);
  };

  const handleOrder = (event) => {
    const value = event.target.value;
    value && dispatch(order(value));
    setRenderOrder(value);
  };    

  return (
    <div className={style.bigContainer}>
      <div className={style.filters}>
        <Genres
          className={style.buttons}
          handleChange={handleChange}
          render={render}
        />
        <Platforms
          className={style.buttons}
          handleChange={handleChange}
          render={render}
        />
        <select className={style.buttons} name="id" onChange={handleChange}>
          <option value="">Filter by storage</option>
          <option value="database">Database</option>
          <option value="api">API</option>
          <option value="All">All</option>
        </select>
        <select className={style.buttons} onChange={handleOrder}>
          <option value="">Order alphabetically</option>
          <option value="a-z">A -{">"} Z</option>
          <option value="z-a">Z -{">"} A</option>
        </select>
        <select className={style.buttons} onChange={handleOrder}>
          <option value="">Order by rating</option>
          <option value="ascending">Ascending</option>
          <option value="descending">Descending</option>
        </select>
      </div>
      <Videogames gameOffset={g
      }/>
    </div>
  );
}

export default Home;
