import { ALL_GAMES, CRETATE_GAME, DELETE_GAME, GAME_BY_ID, UPDATE_GAME } from "./Actions";

const initState = {
  videogames: [],
  videogamesBackUp: [],
  videogameDetail: {}
};

const reducer = (state = initState, { type, payload }) => {
  switch (type) {
    case ALL_GAMES:
      return {
        ...state,
        videogames: [...payload],
        videogamesBackUp: [...payload],
      };
    case GAME_BY_ID:
      return {
        ...state,
        videogameDetail: { ...payload },
      };
    case CRETATE_GAME:
      return {
        ...state,
        videogames: [...videogames, payload],
        videogamesBackUp: [...videogamesBackUp, payload]
      };
    case UPDATE_GAME:
      return {
        ...state,
        videogameDetail: Object.assign(state.videogameDetail, payload)
      }
    default:
      return state;

  }
};

export default reducer;
