import { Link, NavLink } from "react-router-dom";
import { useSelector } from "react-redux";
// import ReactPaginate from "react-paginate";
import style from "./videogame.module.css";
import { useState } from "react";
// import { useEffect } from "react";

// { gameOffset, setGameOffset }
function Videogames({ start, setStart }) {
  const videogames = useSelector((state) => state.videogames);
  const allVideogames = useSelector((state) => state.videogamesBackUp);
  // const active = ({ isActive }) => (isActive ? style.active : undefined);
  // console.log(videogames);
  // console.log(allVideogames);

  // const [current, setCurrent] = useState(15)
  // const [end, setEnd] = useState()
  const [currentPage, setCurrentPage] = useState(1)
  const quantity = 15;
  const total = Math.ceil(videogames.length / quantity);
  const pages = [];

  for (let index = 1; index <= total; index++) {
    pages.push(index)  
  }

  console.log(pages);
  const gamesDisplayed = videogames.slice(start, start + quantity);

  const changePage = (event) => {
    const value = event.target.value;

    setStart(quantity * value);
    // setCurrent(start + quantity)
    // if (videogames.length > quantity ) setStart(start + quantity)
  };
  // useEffect(() => {
  //   setGameOffset(0);
  // }, [videogames]);

  // const gamesPerPage = 15;
  // const endOffset = gameOffset + gamesPerPage;
  // const currentGames = videogames.slice(gameOffset, endOffset);
  // const pageCount = Math.ceil(videogames.length / gamesPerPage);

  // const handlePageClick = (event) => {
  //   const newOffset = (event.selected * gamesPerPage) % videogames.length;
  //   setGameOffset(newOffset);
  // };

  return (
    <>
      <div className={style.cards}>
        {!allVideogames.length ? (
          <div className={style.loading}>
            <span className={style.loadingInner}>Loading...</span>
          </div>
        ) : !videogames.length ? (
          <div className={style.loading}>
            <span className={style.loadingInner}>Games not found</span>
          </div>
        ) : (
          gamesDisplayed.map((games) => {
            return (
              <div className={style.game} key={games.id}>
                <img
                  className={style.image}
                  src={games.image}
                  alt={games.name}
                />
                <div className={style.text}>
                  <Link to={`/detail/${games.id}`}>
                    <h3 className={style.name}>{games.name}</h3>
                  </Link>
                  <div>
                    {games.genres.map((genre, i) => (
                      <>
                        <span key={i}>{genre}</span>{" "}
                        {i < games.genres.length - 1 && "   "}
                      </>
                    ))}
                  </div>
                </div>
              </div>
            );
          })
        )}
      </div>
      <div className={style.paginate}>
        <a>Prev</a>
        {pages.map((page, i) => {
          return (
            <a key={i} className={page === currentPage && style.active} onClick={changePage}>
              {page}
            </a>
          );
        })}
        <a>Next</a>
      </div>

      {/* <ReactPaginate
        previousLabel="< previous"
        nextLabel="next >"
        breakLabel="..."
        onPageChange={handlePageClick}
        pageRangeDisplayed={3}
        marginPagesDisplayed={1}
        pageCount={pageCount}
        renderOnZeroPageCount={null}
        containerClassName={style.paginateContainer}
        pageClassName={style.page}
        activeClassName={style.active}
      /> */}
    </>
  );
}

export default Videogames;
