import Form from "../Form/form";
import { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { gameById, updateGame } from "../../Redux/Actions";

function Update() {
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(gameById(id));
  }, []);
  const details = useSelector((state) => state.videogameDetail);
  const detail = useRef(details.name);
  const [form, setForm] = useState({
    name: details.name,
    image: details.image,
    genres: details.genres,
    platforms: details.platforms,
    rating: details.rating,
    released: details.released,
    description: details.description,
  });

  const handleEdit = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setForm({
      ...form,
      [name]: form[name].filter((elem) => elem !== value),
    });
  };

  console.log(details);
  const handleForm = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    if (name === "rating" && value > 0 && value <= 5) {
      setForm({
        ...form,
        [name]: value,
      });
    } else if (name !== "rating") {
      setForm({
        ...form,
        [name]: value,
      });
    }
  };

  const handleImg = (event) => {
    const file = event.target.files[0];
    setForm({
      ...form,
      image: file,
    });
  };

  const handleOptions = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    if (form[name].includes(value)) return;
    setForm({
      ...form,
      [name]: [...form[name], value],
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData();
    data.append("name", form.name);
    data.append("image", form.image);
    data.append("genres", [...form.genres]);
    data.append("platforms", [...form.platforms]);
    data.append("released", form.released);
    data.append("description", form.description);
    data.append("rating", form.rating);
    dispatch(updateGame(data));
    window.alert("Your videogame was saved successfuly");
  };
  return (
    <div>
      {!form.genres ? <span>Loading...</span> : (
        <Form
          handleEdit={handleEdit}
          handleForm={handleForm}
          handleImg={handleImg}
          handleOptions={handleOptions}
          handleSubmit={handleSubmit}
          name={form.name}
          image={form.image}
          genres={form.genres}
          platforms={form.platforms}
          released={form.released}
          description={form.description}
          rating={form.rating}
        />
      )}
    </div>
  );
}

export default Update;
