    })
  };

  const handleOptions = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    if (form[name].includes(value)) return;
    setForm({
      ...form,
      [name]: [...form[name], value],
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(createGame(form));
    window.alert("Your videogame was saved successfuly");
  };

  console.log(form)
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          onChange={handleForm}
          type="text"
          placeholder="Title"
          name="name"
          value={form.name}
          autoComplete="off"
        />
        <input
          onChange={handleImg}
          type="file"
          placeholder="Upload image"
          name="image"
        />
        <img src={form.image} />
        <Genres handleChange={handleOptions} />
        <div>{form.genres.join(", ")}</div>
        <Platforms handleChange={handleOptions} />
        <div>{form.platforms.join(", ")}</div>
        <input
          onChange={handleForm}
          type="number"
          placeholder="Rating"
          name="rating"
          value={form.rating}
          autoComplete="off"
        />
        <input
          onChange={handleForm}
          type="date"
          placeholder="Released Date"
          name="released"
          value={form.released}
          autoComplete="off"
        />
        <textarea
          onChange={handleForm}
          placeholder="Description..."
          name="description"
          value={form.description}
          autoComplete="off"
        />
        <button>SUBMIT</button>
      </form>
    </div>
  );
}

export default Form;
