import Form from "../Form/form";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { gameById, updateGame } from "../../Redux/Actions";

function Update() {
  const dispatch = useDispatch();
  const { id } = useParams();
  const detail = useSelector((state) => state.videogameDetail);
  
  useEffect(() => {
    dispatch(gameById(id));
  }, []);
  
  const [form, setForm] = useState({
    name: detail.name,
    image: detail.image,
    genres: detail.genres,
    platforms: detail.platforms,
    rating: detail.rating,
    released: detail.released,
    description: detail.description,
  });
  
  console.log();
  const handleForm = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    if (name === "rating" && value > 0 && value <= 5) {
      setForm({
        ...form,
        [name]: value,
      });
    } else if (name !== "rating") {
      setForm({
        ...form,
        [name]: value,
      });
    }
  };

  const handleImg = (event) => {
    const file = event.target.files[0];
    setForm({
      ...form,
      image: file,
    });
  };

  const handleOptions = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    if (form[name].includes(value)) return;
    setForm({
      ...form,
      [name]: [...form[name], value],
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData();
    data.append("name", form.name);
    data.append("image", form.image);
    data.append("genres", [...form.genres]);
    data.append("platforms", [...form.platforms]);
    data.append("released", form.released);
    data.append("description", form.description);
    data.append("rating", form.rating);
    dispatch(updateGame(data));
    window.alert("Your videogame was saved successfuly");
  };
  return (
    <div>
      {detail && (
        <Form
          handleForm={handleForm}
          handleImg={handleImg}
          handleOptions={handleOptions}
          handleSubmit={handleSubmit}
          name={detail.name}
          image={detail.image}
          genres={detail.genres}
          platforms={detail.platforms}
          released={detail.released}
          description={detail.description}
          rating={detail.rating}
        />
      )}
    </div>
  );
}

export default Update;
