import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import style from "./home.module.css";

function Home() {
  const videogames = useSelector((state) => state.videogames);
  console.log(videogames);
  const renderGames = videogames.map((games) => {
    return (
      <div className={style.game} key={games.id}>
        <img className={style.image} src={games.image} alt={games.name} />
        <Link to={`/detail/${games.id}`}>
          <h3>{games.name}</h3>
        </Link>
        <h4>
          {games.genres.map((genre, i) => (
            <>
              <span>{genre}</span> {i < games.genres.length - 1 && ", "}
            </>
          ))}
        </h4>
      </div>
    );
  });

  return <div className={style.home}>{renderGames}</div>;
}

export default Home;
