import { useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { allGames, deleteGame, gameById, restart } from "../../Redux/Actions";
import style from "./detail.module.css";

function Detail() {
  const boolRef = useRef(true);
  const navigate = useNavigate();
  const message = useSelector((state) => state.message);
  const { id } = useParams();
  const uuidRegex =
    /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
  const isUuid = uuidRegex.test(id);
  const dispatch = useDispatch();
  const details = useSelector((state) => state.videogameDetail);

  console.log(details);

  useEffect(() => {
    dispatch(gameById(id));

    return () => {
      if (boolRef.current) {
        dispatch(restart());
      }
    };
  }, []);

  const handleUpdate = () => {
    boolRef.current = false;
    navigate(`/update/${id}`);
  };

  const handleDelete = () => {
    dispatch(deleteGame(id));
    dispatch(allGames());
  };

  return (
    <div className={style.flexContainer}>
      <div className={style.gridContainer}>
        {message ? (
          <span>{message}</span>
        ) : (
          <>
            <h1 className={style.name}>{details.name}</h1>
            <img
              className={style.image}
              src={details.image}
              alt={details.name}
            />
            <div>
              <span>Genres</span>
            {details.genres && (
              <span>
                {details.genres.map((genre, i) => (
                  <>
                    {" " + genre}
                    {i < details.genres.length - 1 && ","}
                  </>
                ))}
              </span>
            )}
            </div>
            
            {details.platforms && (
              <span>
                Platforms
                {details.platforms.map((plat, i) => (
                  <>
                    {" " + plat}
                    {i < details.platforms.length - 1 && ","}
                  </>
                ))}
              </span>
            )}
            <span>Rating {details.rating}</span>
            <span>Released {details.released}</span>
            <span className={style.description}>
              Description
              <p dangerouslySetInnerHTML={{ __html: details.description }} />
            </span>
            {isUuid && (
              <div>
                <button onClick={handleUpdate}>Update Game</button>
                <button onClick={handleDelete}>Delete Game</button>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default Detail;
