import { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { allGames, createGame } from "../../Redux/Actions";
import Form from "../Form/form";

function Create() {
  const previousMessage = useRef(null);
  const message = useSelector((state) => state.message);

  useEffect(() => {
    if (message && message !== previousMessage.current) {
      alert(message);
      previousMessage.current = message;
    }
    return () => {
      dispatch(res)
    }
  }, [message]);

  console.log(message);
  const dispatch = useDispatch();
  const [form, setForm] = useState({
    name: "",
    image: "",
    genres: [],
    platforms: [],
    rating: "",
    released: "",
    description: "",
  });

  const handleEdit = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setForm({
      ...form,
      [name]: form[name].filter((elem) => elem !== value),
    });
  };

  const handleForm = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    if (name === "rating" && value > 0 && value <= 5) {
      setForm({
        ...form,
        [name]: value,
      });
    } else if (name !== "rating") {
      setForm({
        ...form,
        [name]: value,
      });
    }
  };

  const handleImg = (event) => {
    const file = event.target.files[0];
    setForm({
      ...form,
      image: file,
    });
  };

  const handleOptions = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    if (form[name].includes(value)) return;
    setForm({
      ...form,
      [name]: [...form[name], value],
    });
  };
  console.log(form.image);

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData();
    data.append("name", form.name);
    data.append("image", form.image);
    data.append("genres", [...form.genres]);
    data.append("platforms", [...form.platforms]);
    data.append("released", form.released);
    data.append("description", form.description);
    data.append("rating", form.rating);
    dispatch(createGame(data));
    dispatch(allGames());
  };
  return (
    <div>
      <Form
        name={form.name}
        image={form.image}
        genres={form.genres}
        platforms={form.platforms}
        released={form.released}
        description={form.description}
        rating={form.rating}
        handleEdit={handleEdit}
        handleForm={handleForm}
        handleOptions={handleOptions}
        handleImg={handleImg}
        handleSubmit={handleSubmit}
      />
    </div>
  );
}

export default Create;
